import React from "react";
import Firebase from "firebase";

export default function Formcontact() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [loader, setLoader] = useState(false);
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoader(true);
    db.collection("contacts")
      .add({
        name: name,
        email: email,
        message: message,
      })
      .then(() => {
        alert("Mensagem enviada com sucesso ðŸ˜‰.");
        setLoader(false);
      })
      .catch((error) => {
        alert("NÃ£o foi possivel enviar sua mensagem ðŸ˜­." + error.message);
        setLoader(false);
      });

    setName("");
    setEmail("");
    setMessage("");
  };
  return (
    <div className="div-contatodev">
      <form className="form" onSubmit={handleSubmit}>
        <h2>Entre em contato com o desenvolvedor</h2>

        <div className="form-camp">
          <div className="form-camp-header">
            <i className="far fa-user"></i>
            <label>Nome</label>
          </div>
          <input
            className="form-input"
            type="text"
            placeholder="Nome"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div className="form-camp">
          <div className="form-camp-header">
            <i className="far fa-envelope"></i>
            <label>Email</label>
          </div>
          <input
            className="form-input"
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-camp">
          <div className="form-camp-header">
            <i className="far fa-pen"></i>
            <label>Mensagem</label>
          </div>
          <textarea
            className="form-input"
            type="text"
            placeholder="Mensagem"
            rows="4"
            cols="30"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            required
          ></textarea>
        </div>
        <button
          type="submit"
          style={{ background: loader ? "#00E676" : "#1E88E5" }}
        >
          {loader ? "Enviando" : "Enviar"}
        </button>
      </form>
    </div>
  );
}
